import React, { useEffect, useRef, useState } from 'react'
import { TextField , Button } from '@mui/material';
import "../styles/VideoMeetComponent.css"
import io from "socket.io-client"
// import { connect } from 'mongoose';
// import { connectToSocket } from '../../../backend/src/controller/socketManager';
const server_url="http://localhost:8000"
var connections={};
const peerConfigConnections={
    "iceServers":[
        {"urls":"stun:stun.l.google.com:19032"}
    ]
}
export default function VideoMeetComponent() {
    var socketRef=useRef();
    let socketIdRef=useRef();
    let localVideoRef=useRef()
    let [videoAvailable , setvideoAvailable]=useState(true);
    let [audioAvailable , setaudioAvailable]=useState(true);
    let [video , setVideo]=useState([]);
    let [audio , setAudio]=useState();
    let [screen , setScreen]=useState();
    let [showModel , setModel]=useState();
    let [screenAvailable , setScreenAvailable]=useState();
    let [message , setMessage]=useState("");
    let [messages , setMessages]=useState([]);
    let [newMessages , setNewMessages]=useState(0);
    let [askForUserName, setAskforUserName]=useState(true);
    let [username , setUserName]=useState("");
    const videoRef = useRef([]);
    let [videos , setVideos]=useState([])

    const getPermissions = async()=>{
        try{
            const videoPermission=await navigator.mediaDevices.getUserMedia({video:true})
            if(videoPermission){
                setvideoAvailable(true)
            }else{
                setvideoAvailable(false)
            }
            const audioPermission=await navigator.mediaDevices.getUserMedia({audio:true})
            if(audioPermission){
                setaudioAvailable(true)
            }else{
                setaudioAvailable(false);
            }
            if(navigator.mediaDevices.getDisplayMedia){
                setScreenAvailable(true)
            }else{
                setScreenAvailable(false);
            }
            if(videoAvailable || audioAvailable){
                const userMediaStream=await navigator.mediaDevices.getUserMedia({video:videoAvailable , audio:audioAvailable})
                if(userMediaStream){
                    window.localStream =userMediaStream
                    if(localVideoRef.current){
                        localVideoRef.current.srcObject=userMediaStream
                    }
                }


            }

        }catch (err){
            console.log(err);

        }

    }
useEffect(()=>{
    getPermissions()
},[])
let getUserMediaSuccess=(stream)=>{


}



let getUserMedia=()=>{
    if((video && videoAvailable) || (audio &&audioAvailable)){
        navigator.mediaDevices.getUserMedia({video:video , audio:audio})
        .then((getUserMediaSuccess)=>{})
        .then((stream)=>{})
        .catch((err)=>{console.log(err)});
    }else{
        try{
            let tracks = localVideoRef.current.srcObject.getTracks();
            tracks.forEach(track => track.stop()
                
            );
        }catch (err){
            console.log(err);

        }
    }
}
useEffect(()=>{
    if(video !== undefined && audio !== undefined ){
        getUserMedia()
    }
},[audio,video])
let gotMessageFromServer=(fromId , message)=>{

}
let addMessages=()=>{

}
  let connectToSocketServer = () => {
        socketRef.current = io.connect(server_url, { secure: false })
        socketRef.current.on('signal', gotMessageFromServer);
        socketRef.current.on("connect",()=>{
            socketRef.current.emit("join-call",window.location.href)
            socketRef.current=socketRef.current.id 
            socketRef.current.on('chat-message',addMessages);
            socketRef.current.on("user-left",(id)=>{
                setVideos((videos)=>videos.filter((video)=>video.socketId !== id))
            })
            
            socketRef.current.on("user-joined",(id , clients)=>{
                clients.forEach((socketListId)=>{
                    connections[socketListId]=new RTCPeerConnection(peerConfigConnections)
                    connections[socketListId].onicecandidate=(event)=>{
                        if(event.candidate !== null){
                            socketRef.current.emit("signal",socketListId , JSON.stringify({'ice':event.candidate}))

                        }
                    }
                    connections[socketListId].onaddstream = (event)=>{
                        let videoExists = videoRef.current.find(video=> video.socketId === socketListId);

                        if(videoExists){
                            setVideo(videos=>{
                                const updatedVideos = video.map(video=>
                                    video.socketId === socketListId ? { ...video , stream:event.stream} :video
                                )
                                videoRef.current=updatedVideos;
                                return updatedVideos;
                            })
                        }else{
                            let newVideo={
                                socketId:socketListId,
                                stream:event.stream,
                                autoPlay:true,
                                playsinline:true
                            }
                            setVideos(videos=>{
                                const updatedVideos=[ ...videos, newVideo];
                                videoRef.current=updatedVideos;
                                return updatedVideos;
                            })
                        }
                    }
                })
            })
        })
  }
 
let getMedia=()=>{
    setVideo(videoAvailable);
    setAudio(audioAvailable);
    // connectToSocketServer();
}
let connect = () => {
        setAskforUserName(false);
        getMedia();
    }



    return ( 
        
        <div>
            {askForUserName===true?
            <div>
                <h2>Enter into Lobby</h2>
                <TextField id="outlined-basic" label="username" value={username} onChange={e => setUserName(e.target.value)}  variant="outlined" />
                <Button onClick={connect} variant="contained">Join Meeting</Button>
                <div>
                    <video  ref={localVideoRef} autoPlay muted ></video>
                    </div>
                </div>:<></>
            }

        </div>
        
     );
}
